import React from 'react'
import './Product.css'
import {useStateValue} from "./StateProvider"

function Product({id, title, image, price, rating}) {

    // after we click the button, we trigger below functions
    // and dispatch state along with the data
    const [{ basket }, dispatch] = useStateValue();
    const addToBasket = () => {
        dispatch({
            type: "ADD_TO_BASKET",
            item: {
                id: id,
                title: title,
                image: image,
                price: price,
                rating: rating,
            },
        });
    };

